CREATE TABLE `publishers` (
`id`int(11) NOT NULL,
`shortname`varchar(100) NOT NULL DEFAULT '',
`website`varchar(200) DEFAULT NULL,
`amateur`tinyint(1) NOT NULL DEFAULT '0',
`country`char(3) DEFAULT NULL,
`fullname`varchar(200) DEFAULT NULL,
`notes`text,
`hw_notes`text,
`main_company`int(11) DEFAULT '0',
`sub_company`int(11) DEFAULT '0',
`address`text,
`birthyear`int(11) DEFAULT '0',
`deathyear`int(11) DEFAULT '0',
`developer_view`int(11) DEFAULT '0',
`publisher_view`int(11) DEFAULT '0',
`gamelist`tinyint(1) DEFAULT '0',
`has_logo`tinyint(4) NOT NULL DEFAULT '0',
`insert_date`date NOT NULL,
`updated_on`date NOT NULL,
`insert_by`int(11) NOT NULL DEFAULT '0',
`updated_by`int(11) NOT NULL DEFAULT '0',
PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1 COLLATE=latin1_spanish_ci;
















